import React, { useState } from 'react'
import { connect } from 'react-redux'
import { getProjects, postProject, patchProject, deleteProject } from '../store/actions/projectActions'
import '../styles/allProjects.css'
import ProjectForm from '../components/ProjectForm'
import ProjectItem from '../components/ProjectItem'
import { removeError } from '../store/actions/errorActions'
import Table from 'react-bootstrap/Table'
import Modal from "react-bootstrap/Modal"

function AllProjects({ projects, users, errors, removeError, postProject, history, patchProject, deleteProject }) {
    const [edit, setEdit] = useState(false)
    const [projectId, setProjectId] = useState("")
    const [show, setShow] = useState(false);

    let projectsMap = projects.map((project, i) =>
        <ProjectItem
            key={i}
            setProjectId={setProjectId}
            setEdit={setEdit}
            index={i + 1}
            history={history}
            project={project}
        />)

    const handleShow = () => setShow(true);
    history.listen(() => {
        removeError()
    })

    const projectDetails = projects.find(project => project._id === projectId)

    return (
        <div className="allProjectsContainer">
            <h1 className="display-6">Projects</h1>
            <button onClick={() => { setEdit(false); handleShow() }} type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#projectModal">
                Create
            </button>
            <div className="mappedProjects">
                <Table hover>
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Title</th>
                            <th scope="col">Start Date</th>
                            <th scope="col"></th>
                        </tr>
                    </thead>
                    <tbody>
                        {projectsMap}
                    </tbody>
                </Table>
            </div>
            <div className="projectForm">
                <ProjectForm
                    edit={edit}
                    postProject={postProject}
                    patchProject={patchProject}
                    projectId={projectId}
                    users={users}
                    errors={errors}
                    project={projectDetails}
                    setProjectId={setProjectId}
                    show={show}
                    setShow={setShow}
                    removeError={removeError}
                />
            </div>
            {/* <div className="modal" id="removeModal" tabIndex="-1">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title">Confirm Delete</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <button onClick={() => deleteProject(projectId)} type="button" className="btn btn-secondary" data-bs-dismiss="modal">Remove</button>
                        </div>
                    </div>
                </div>
            </div> */}
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        projects: state.projects,
        errors: state.errors,
        users: state.users,
    }
}

export default connect(mapStateToProps, { getProjects, postProject, patchProject, deleteProject, removeError })(AllProjects)
