import React, { useState } from 'react'
import { connect } from 'react-redux'
import { postIssue } from '../store/actions/issueActions'
import IssuesForm from '../components/IssuesForm'
import IssueItem from '../components/IssueItem'
import '../styles/AllIssues.css'

function MyIssues({ issues, postIssue, history }) {
    //have a true or false state object not a toggle and a issueId staate
    const [edit, setEdit] = useState(false)
    const [issueId, setIssueId] = useState("")
    const mappedIssues = issues.map((issue, i) => (
        <IssueItem
            issue={issue}
            key={i}
            index={i + 1}
            history={history}
            setEdit={setEdit}
            setIssueId={setIssueId}
        />
    ))

    return (
        <div className="allIssuesContainer">
            <h1 className="display-6">Issues</h1>
            <button onClick={() => setEdit(false)} type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#issueModal">
                Create Issue
            </button>
            <div className="mappedIssues">
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Title</th>
                            <th scope="col">Project Name</th>
                            <th scope="col">Developer</th>
                            <th scope="col">Priority</th>
                            <th scope="col">Status</th>
                            <th scope="col">Type</th>
                            <th scope="col">Created</th>
                            <th scope="col"></th>
                        </tr>
                    </thead>
                    <tbody>
                        {mappedIssues}
                    </tbody>
                </table>
            </div>
            <div>
                <IssuesForm
                    postIssue={postIssue}
                    edit={edit}
                />
            </div>
            <div className="modal" id="removeModal" tabIndex="-1">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title">Hello Confirm Delete</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Remove</button>
                        </div>
                    </div>
                </div>
            </div>
            <button data-bs-toggle="modal" data-bs-target="#issueModal" name="edit" >edit</button>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        issues: state.issues,
        errors: state.errors,
    }
}

export default connect(mapStateToProps, { postIssue })(MyIssues)
