import axios from "axios";
import { apiCall } from "../../services/api";

//api call syntax apiCall(method, path, payload)

export function getIssues(dispatch) {
    return apiCall("get", "/api/issues")
        .then(res => {
            console.log(res)
            dispatch({ type: "LOAD_ISSUES"})
        })
}

export function postIssue() {
    return apiCall("post", )
}
