import React from 'react';
import { Route, Switch } from 'react-router-dom';
import Main from '../components/Main'
import AuthForm from '../components/AuthForm'
import Test from '../components/Test'
import ProjectPage from '../components/ProjectPage'
import AllProjects from '../components/AllProjects'
import { connect } from 'react-redux';
import { authUser } from '../store/actions/userActions';
import "../App.css"

function App(props) {
  const { currentUser, errors, authUser, projects } = props
  console.log(projects)
  return (
    <div>
      <Switch>
        <Route exact path="/" render={routeProps => <Main {...routeProps} currentUser={currentUser} />} />
        <Route exact path="/login"
          render={routeProps =>
            <AuthForm
              errors={errors}
              authUser={authUser}
              {...routeProps}
            />
          }
        />
        <Route exact path="/signup"
          render={routeProps =>
            <AuthForm
              authUser={authUser}
              errors={errors}
              signup
              {...routeProps}
            />
          }
        />
        <Route exact path="/test" render={() => <Test />} />
        <Route exact path="/projects/:projectId" render={(routeProps) => <ProjectPage project={projects[routeProps.match.params.projectId]} {...routeProps} />} />
        <Route exact path="/projects" render={(routeProps) => <AllProjects {...routeProps} />} />
      </Switch>
    </div>
  )
}

const mapStateToProps = (state) => {
  console.log(state)
  return {
    currentUser: state.currentUser,
    errors: state.errors,
    projects: state.projects,
  }
}

export default connect(mapStateToProps, { authUser })(App);
