import React from 'react'

function IssueItem({ issue, index, history, setEdit, setIssueId }) {
    const newDate = new Date(issue.createdAt).toDateString()
    const handleClick = (e) => {
        switch (e.target.name) {
            case "view":
                history.push(`/projects/${issue._id}`)
                break
            case "edit":
                setEdit(true)
                setIssueId(issue._id)
                break
            case "remove": 
                // setProjectId(issue._id)    
        }   
    }
    return (
        <tr>
            <th scope="row">{index}</th>
            <td>{ issue.title }</td>
            <td>{ issue.project }</td>
            <td>{ issue.user.username }</td>
            <td>{ issue.priority }</td>
            <td>{ issue.status }</td>
            <td>{ issue.type }</td>
            <td>{ newDate }</td>
            <td>
                {
                    <>
                        <button name="view" onClick={handleClick}>View</button>
                        <button data-bs-toggle="modal" data-bs-target="#projectModal" name="edit" onClick={handleClick}>edit</button>
                        <button data-bs-toggle="modal" data-bs-target="#removeModal" name="remove" onClick={handleClick}>remove</button>
                    </>
                }
            </td>
        </tr>
    )
}

export default IssueItem
